import { hashPassword } from './auth';
import { storage } from './storage';

async function seedDatabase() {
  console.log('ðŸŒ± Starting database seeding...');

  try {
    // Create default admin user
    const existingAdmin = await storage.getAdminUserByEmail('admin@example.com');
    if (!existingAdmin) {
      const hashedPassword = await hashPassword('ChangeMe!2025');
      await storage.createAdminUser({
        email: 'admin@example.com',
        password: hashedPassword,
        name: 'Admin User',
        role: 'admin',
        mustChangePassword: true
      });
      console.log('âœ… Created default admin user (admin@example.com / ChangeMe!2025)');
    } else {
      console.log('â„¹ï¸  Admin user already exists');
    }

    // Create sample categories
    const categories = [
      { name: 'Electronics', slug: 'electronics', description: 'Phones, laptops, and electronic devices' },
      { name: 'Fashion', slug: 'fashion', description: 'Clothing, shoes, and accessories' },
      { name: 'Home & Garden', slug: 'home-garden', description: 'Furniture, appliances, and home decor' },
      { name: 'Books', slug: 'books', description: 'Educational and entertainment books' },
      { name: 'Sports', slug: 'sports', description: 'Sports equipment and fitness gear' }
    ];

    const createdCategories = [];
    for (const categoryData of categories) {
      const existingCategories = await storage.getAllCategories();
      const exists = existingCategories.some(cat => cat.slug === categoryData.slug);

      if (!exists) {
        const category = await storage.createCategory(categoryData);
        createdCategories.push(category);
        console.log(`âœ… Created category: ${category.name}`);
      } else {
        const existing = existingCategories.find(cat => cat.slug === categoryData.slug);
        createdCategories.push(existing!);
        console.log(`â„¹ï¸  Category already exists: ${categoryData.name}`);
      }
    }

    // Create sample products
    const products = [
      {
        title: 'iPhone 15 Pro Max',
        sku: 'IPH-15PM-128',
        description: 'Latest Apple smartphone with 128GB storage, advanced camera system, and A17 Pro chip',
        priceGhs: '8500.00',
        stock: 25,
        categoryId: createdCategories.find(c => c.slug === 'electronics')?.id,
        images: ['https://images.unsplash.com/photo-1511707171634-5f897ff02aa9?w=400'],
        tags: ['smartphone', 'apple', 'premium'],
        isActive: true
      },
      {
        title: 'Samsung Galaxy S24',
        sku: 'SAM-S24-256',
        description: 'Samsung flagship with 256GB storage, AI photography, and long-lasting battery',
        priceGhs: '6800.00',
        stock: 18,
        categoryId: createdCategories.find(c => c.slug === 'electronics')?.id,
        images: ['https://images.unsplash.com/photo-1610945265064-0e34e5519bbf?w=400'],
        tags: ['smartphone', 'samsung', 'android'],
        isActive: true
      },
      {
        title: 'Premium Cotton T-Shirt',
        sku: 'TSH-COT-001',
        description: 'Comfortable everyday wear made from 100% organic cotton, available in multiple colors',
        priceGhs: '45.00',
        stock: 5,
        categoryId: createdCategories.find(c => c.slug === 'fashion')?.id,
        images: ['https://images.unsplash.com/photo-1521572163474-6864f9cf17ab?w=400'],
        tags: ['clothing', 'cotton', 'casual'],
        isActive: true
      },
      {
        title: 'Automatic Coffee Maker',
        sku: 'COF-MKR-12C',
        description: '12-cup programmable coffee maker with timer, auto-shutoff, and thermal carafe',
        priceGhs: '320.00',
        stock: 0,
        categoryId: createdCategories.find(c => c.slug === 'home-garden')?.id,
        images: ['https://images.unsplash.com/photo-1586023492125-27b2c045efd7?w=400'],
        tags: ['appliance', 'coffee', 'kitchen'],
        isActive: false
      },
      {
        title: 'JavaScript Programming Guide',
        sku: 'BOK-JS-001',
        description: 'Complete guide to modern JavaScript programming with practical examples and projects',
        priceGhs: '85.00',
        stock: 50,
        categoryId: createdCategories.find(c => c.slug === 'books')?.id,
        images: ['https://images.unsplash.com/photo-1544716278-ca5e3f4abd8c?w=400'],
        tags: ['programming', 'javascript', 'education'],
        isActive: true
      }
    ];

    const existingProducts = await storage.getAllProducts();
    for (const productData of products) {
      const exists = existingProducts.some(p => p.sku === productData.sku);
      if (!exists) {
        await storage.createProduct(productData);
        console.log(`âœ… Created product: ${productData.title}`);
      } else {
        console.log(`â„¹ï¸  Product already exists: ${productData.title}`);
      }
    }

    // Create sample coupons
    const coupons = [
      {
        code: 'WELCOME10',
        name: 'Welcome Discount',
        description: 'Get 10% off your first order',
        type: 'percent',
        value: '10',
        minOrderAmount: '50.00',
        maxUses: 100,
        isActive: true,
        expiresAt: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000) // 30 days from now
      },
      {
        code: 'SAVE50',
        name: 'Fixed Discount',
        description: 'Save GHS 50 on orders over GHS 300',
        type: 'fixed',
        value: '50.00',
        minOrderAmount: '300.00',
        maxUses: 50,
        isActive: true,
        expiresAt: new Date(Date.now() + 60 * 24 * 60 * 60 * 1000) // 60 days from now
      }
    ];

    const existingCoupons = await storage.getAllCoupons();
    for (const couponData of coupons) {
      const exists = existingCoupons.some(c => c.code === couponData.code);
      if (!exists) {
        await storage.createCoupon(couponData);
        console.log(`âœ… Created coupon: ${couponData.code}`);
      } else {
        console.log(`â„¹ï¸  Coupon already exists: ${couponData.code}`);
      }
    }

    console.log('ðŸŽ‰ Database seeding completed successfully!');
    console.log('');
    console.log('ðŸ“‹ Summary:');
    console.log('- Admin user: admin@example.com / ChangeMe!2025');
    console.log('- Categories: 5 created');
    console.log('- Products: 5 created');
    console.log('- Coupons: 2 created');
    console.log('');
    console.log('âš ï¸  Remember to change the admin password on first login!');

  } catch (error) {
    console.error('âŒ Error seeding database:', error);
    throw error;
  }
}

// Run seeding if this file is executed directly
if (import.meta.url === `file://${process.argv[1]}`) {
  seedDatabase()
    .then(() => process.exit(0))
    .catch((error) => {
      console.error(error);
      process.exit(1);
    });
}

export { seedDatabase };

server/storage.ts.content:
import { 
  adminUsers, 
  users, 
  categories, 
  products, 
  orders, 
  orderItems, 
  payments, 
  coupons, 
  auditLogs,
  type AdminUser, 
  type InsertAdminUser,
  type User, 
  type InsertUser,
  type Category,
  type InsertCategory,
  type Product,
  type InsertProduct,
  type Order,
  type InsertOrder,
  type OrderItem,
  type InsertOrderItem,
  type Payment,
  type InsertPayment,
  type Coupon,
  type InsertCoupon,
  type AuditLog,
  type InsertAuditLog
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and, gte, sql, count } from "drizzle-orm";
import session from "express-session";
import connectPg from "connect-pg-simple";
import { type SessionStore } from "express-session";
import { pool } from "./db";

const PostgresSessionStore = connectPg(session);

export interface IStorage {
  sessionStore: SessionStore;

  // Admin Users
  getAdminUser(id: string): Promise<AdminUser | undefined>;
  getAdminUserByEmail(email: string): Promise<AdminUser | undefined>;
  createAdminUser(user: InsertAdminUser): Promise<AdminUser>;
  updateAdminUser(id: string, updates: Partial<InsertAdminUser>): Promise<AdminUser | undefined>;

  // Telegram Users
  getUser(id: string): Promise<User | undefined>;
  getUserByTelegramId(telegramId: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUser(id: string, updates: Partial<InsertUser>): Promise<User | undefined>;
  getAllUsers(): Promise<User[]>;

  // Categories
  getAllCategories(): Promise<Category[]>;
  createCategory(category: InsertCategory): Promise<Category>;
  updateCategory(id: string, updates: Partial<InsertCategory>): Promise<Category | undefined>;
  deleteCategory(id: string): Promise<boolean>;

  // Products
  getAllProducts(): Promise<Product[]>;
  getProduct(id: string): Promise<Product | undefined>;
  getProductsByCategoryId(categoryId: string): Promise<Product[]>;
  searchProducts(query: string): Promise<Product[]>;
  createProduct(product: InsertProduct): Promise<Product>;
  updateProduct(id: string, updates: Partial<InsertProduct>): Promise<Product | undefined>;
  deleteProduct(id: string): Promise<boolean>;
  updateProductStock(id: string, quantity: number): Promise<Product | undefined>;

  // Orders
  getAllOrders(): Promise<Order[]>;
  getOrder(id: string): Promise<Order | undefined>;
  getOrdersByUserId(userId: string): Promise<Order[]>;
  createOrder(order: InsertOrder): Promise<Order>;
  updateOrder(id: string, updates: Partial<InsertOrder>): Promise<Order | undefined>;
  generateOrderNumber(): Promise<string>;

  // Order Items
  getOrderItems(orderId: string): Promise<OrderItem[]>;
  createOrderItem(item: InsertOrderItem): Promise<OrderItem>;
  deleteOrderItems(orderId: string): Promise<boolean>;

  // Payments
  getAllPayments(): Promise<Payment[]>;
  getPayment(id: string): Promise<Payment | undefined>;
  getPaymentByIdempotencyKey(key: string): Promise<Payment | undefined>;
  getPaymentsByOrderId(orderId: string): Promise<Payment[]>;
  getPendingPayments(): Promise<Payment[]>;
  createPayment(payment: InsertPayment): Promise<Payment>;
  updatePayment(id: string, updates: Partial<InsertPayment>): Promise<Payment | undefined>;

  // Coupons
  getAllCoupons(): Promise<Coupon[]>;
  getCoupon(id: string): Promise<Coupon | undefined>;
  getCouponByCode(code: string): Promise<Coupon | undefined>;
  createCoupon(coupon: InsertCoupon): Promise<Coupon>;
  updateCoupon(id: string, updates: Partial<InsertCoupon>): Promise<Coupon | undefined>;
  deleteCoupon(id: string): Promise<boolean>;

  // Analytics
  getDashboardMetrics(): Promise<{
    totalOrders: number;
    revenue: string;
    customers: number;
    mtnPayments: number;
  }>;

  // Audit Logs
  createAuditLog(log: InsertAuditLog): Promise<AuditLog>;
}

export class DatabaseStorage implements IStorage {
  sessionStore: SessionStore;

  constructor() {
    this.sessionStore = new PostgresSessionStore({ 
      pool, 
      createTableIfMissing: true 
    });
  }

  // Admin Users
  async getAdminUser(id: string): Promise<AdminUser | undefined> {
    const [user] = await db.select().from(adminUsers).where(eq(adminUsers.id, id));
    return user || undefined;
  }

  async getAdminUserByEmail(email: string): Promise<AdminUser | undefined> {
    const [user] = await db.select().from(adminUsers).where(eq(adminUsers.email, email));
    return user || undefined;
  }

  async createAdminUser(user: InsertAdminUser): Promise<AdminUser> {
    const [newUser] = await db
      .insert(adminUsers)
      .values(user)
      .returning();
    return newUser;
  }

  async updateAdminUser(id: string, updates: Partial<InsertAdminUser>): Promise<AdminUser | undefined> {
    const [updated] = await db
      .update(adminUsers)
      .set({ ...updates, updatedAt: sql`now()` })
      .where(eq(adminUsers.id, id))
      .returning();
    return updated || undefined;
  }

  // Telegram Users
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByTelegramId(telegramId: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.telegramId, telegramId));
    return user || undefined;
  }

  async createUser(user: InsertUser): Promise<User> {
    const [newUser] = await db
      .insert(users)
      .values(user)
      .returning();
    return newUser;
  }

  async updateUser(id: string, updates: Partial<InsertUser>): Promise<User | undefined> {
    const [updated] = await db
      .update(users)
      .set({ ...updates, updatedAt: sql`now()` })
      .where(eq(users.id, id))
      .returning();
    return updated || undefined;
  }

  async getAllUsers(): Promise<User[]> {
    return await db.select().from(users).orderBy(desc(users.createdAt));
  }

  // Categories
  async getAllCategories(): Promise<Category[]> {
    return await db.select().from(categories).where(eq(categories.isActive, true));
  }

  async createCategory(category: InsertCategory): Promise<Category> {
    const [newCategory] = await db
      .insert(categories)
      .values(category)
      .returning();
    return newCategory;
  }

  async updateCategory(id: string, updates: Partial<InsertCategory>): Promise<Category | undefined> {
    const [updated] = await db
      .update(categories)
      .set({ ...updates, updatedAt: sql`now()` })
      .where(eq(categories.id, id))
      .returning();
    return updated || undefined;
  }

  async deleteCategory(id: string): Promise<boolean> {
    const result = await db
      .update(categories)
      .set({ isActive: false, updatedAt: sql`now()` })
      .where(eq(categories.id, id));
    return result.rowCount! > 0;
  }

  // Products
  async getAllProducts(): Promise<Product[]> {
    return await db.select().from(products).orderBy(desc(products.createdAt));
  }

  async getProduct(id: string): Promise<Product | undefined> {
    const [product] = await db.select().from(products).where(eq(products.id, id));
    return product || undefined;
  }

  async getProductsByCategoryId(categoryId: string): Promise<Product[]> {
    return await db
      .select()
      .from(products)
      .where(and(eq(products.categoryId, categoryId), eq(products.isActive, true)));
  }

  async searchProducts(query: string): Promise<Product[]> {
    return await db
      .select()
      .from(products)
      .where(
        and(
          eq(products.isActive, true),
          sql`${products.title} ILIKE ${`%${query}%`} OR ${products.sku} ILIKE ${`%${query}%`}`
        )
      );
  }

  async createProduct(product: InsertProduct): Promise<Product> {
    const [newProduct] = await db
      .insert(products)
      .values(product)
      .returning();
    return newProduct;
  }

  async updateProduct(id: string, updates: Partial<InsertProduct>): Promise<Product | undefined> {
    const [updated] = await db
      .update(products)
      .set({ ...updates, updatedAt: sql`now()` })
      .where(eq(products.id, id))
      .returning();
    return updated || undefined;
  }

  async deleteProduct(id: string): Promise<boolean> {
    const result = await db
      .update(products)
      .set({ isActive: false, updatedAt: sql`now()` })
      .where(eq(products.id, id));
    return result.rowCount! > 0;
  }

  async updateProductStock(id: string, quantity: number): Promise<Product | undefined> {
    const [updated] = await db
      .update(products)
      .set({ stock: sql`${products.stock} + ${quantity}`, updatedAt: new Date() })
      .where(eq(products.id, id))
      .returning();
    return updated || undefined;
  }

  // Orders
  async getAllOrders(): Promise<Order[]> {
    return await db.select().from(orders).orderBy(desc(orders.createdAt));
  }

  async getOrder(id: string): Promise<Order | undefined> {
    const [order] = await db.select().from(orders).where(eq(orders.id, id));
    return order || undefined;
  }

  async getOrdersByUserId(userId: string): Promise<Order[]> {
    return await db
      .select()
      .from(orders)
      .where(eq(orders.userId, userId))
      .orderBy(desc(orders.createdAt));
  }

  async createOrder(order: InsertOrder): Promise<Order> {
    const orderNumber = await this.generateOrderNumber();
    const [newOrder] = await db
      .insert(orders)
      .values({ ...order, orderNumber })
      .returning();
    return newOrder;
  }

  async updateOrder(id: string, updates: Partial<InsertOrder>): Promise<Order | undefined> {
    const [updated] = await db
      .update(orders)
      .set({ ...updates, updatedAt: sql`now()` })
      .where(eq(orders.id, id))
      .returning();
    return updated || undefined;
  }

  async generateOrderNumber(): Promise<string> {
    const now = new Date();
    const year = now.getFullYear();
    const month = String(now.getMonth() + 1).padStart(2, '0');
    const day = String(now.getDate()).padStart(2, '0');

    const [{ value: count }] = await db
      .select({ value: sql<number>`count(*)` })
      .from(orders)
      .where(gte(orders.createdAt, new Date(year, now.getMonth(), now.getDate())));

    const sequence = String(count + 1).padStart(3, '0');
    return `ORD-${year}${month}${day}-${sequence}`;
  }

  // Order Items
  async getOrderItems(orderId: string): Promise<OrderItem[]> {
    return await db.select().from(orderItems).where(eq(orderItems.orderId, orderId));
  }

  async createOrderItem(item: InsertOrderItem): Promise<OrderItem> {
    const [newItem] = await db
      .insert(orderItems)
      .values(item)
      .returning();
    return newItem;
  }

  async deleteOrderItems(orderId: string): Promise<boolean> {
    const result = await db.delete(orderItems).where(eq(orderItems.orderId, orderId));
    return result.rowCount! > 0;
  }

  // Payments
  async getAllPayments(): Promise<Payment[]> {
    return await db.select().from(payments).orderBy(desc(payments.createdAt));
  }

  async getPayment(id: string): Promise<Payment | undefined> {
    const [payment] = await db.select().from(payments).where(eq(payments.id, id));
    return payment || undefined;
  }

  async getPaymentByIdempotencyKey(key: string): Promise<Payment | undefined> {
    const [payment] = await db.select().from(payments).where(eq(payments.idempotencyKey, key));
    return payment || undefined;
  }

  async getPaymentsByOrderId(orderId: string): Promise<Payment[]> {
    return await db.select().from(payments).where(eq(payments.orderId, orderId));
  }

  async getPendingPayments(): Promise<Payment[]> {
    return await db
      .select()
      .from(payments)
      .where(eq(payments.status, 'PENDING'));
  }

  async createPayment(payment: InsertPayment): Promise<Payment> {
    const [newPayment] = await db
      .insert(payments)
      .values(payment)
      .returning();
    return newPayment;
  }

  async updatePayment(id: string, updates: Partial<InsertPayment>): Promise<Payment | undefined> {
    const [updated] = await db
      .update(payments)
      .set({ ...updates, updatedAt: sql`now()` })
      .where(eq(payments.id, id))
      .returning();
    return updated || undefined;
  }

  // Coupons
  async getAllCoupons(): Promise<Coupon[]> {
    return await db.select().from(coupons).orderBy(desc(coupons.createdAt));
  }

  async getCoupon(id: string): Promise<Coupon | undefined> {
    const [coupon] = await db.select().from(coupons).where(eq(coupons.id, id));
    return coupon || undefined;
  }

  async getCouponByCode(code: string): Promise<Coupon | undefined> {
    const [coupon] = await db.select().from(coupons).where(eq(coupons.code, code));
    return coupon || undefined;
  }

  async createCoupon(coupon: InsertCoupon): Promise<Coupon> {
    const [newCoupon] = await db
      .insert(coupons)
      .values(coupon)
      .returning();
    return newCoupon;
  }

  async updateCoupon(id: string, updates: Partial<InsertCoupon>): Promise<Coupon | undefined> {
    const [updated] = await db
      .update(coupons)
      .set({ ...updates, updatedAt: sql`now()` })
      .where(eq(coupons.id, id))
      .returning();
    return updated || undefined;
  }

  async deleteCoupon(id: string): Promise<boolean> {
    const result = await db
      .update(coupons)
      .set({ isActive: false, updatedAt: sql`now()` })
      .where(eq(coupons.id, id));
    return result.rowCount! > 0;
  }

  // Analytics
  async getDashboardMetrics() {
    const [ordersCount] = await db
      .select({ value: count() })
      .from(orders);

    const [revenueSum] = await db
      .select({ value: sql<string>`COALESCE(SUM(${orders.totalGhs}), 0)` })
      .from(orders)
      .where(eq(orders.status, 'PAID'));

    const [customersCount] = await db
      .select({ value: count() })
      .from(users);

    const [mtnPaymentsCount] = await db
      .select({ value: count() })
      .from(payments)
      .where(eq(payments.status, 'SUCCESS'));

    return {
      totalOrders: ordersCount.value,
      revenue: revenueSum.value || '0',
      customers: customersCount.value,
      mtnPayments: mtnPaymentsCount.value
    };
  }

  // Audit Logs
  async createAuditLog(log: InsertAuditLog): Promise<AuditLog> {
    const [newLog] = await db
      .insert(auditLogs)
      .values(log)
      .returning();
    return newLog;
  }
}

export const storage = new DatabaseStorage();

