import { Request, Response } from 'express';
import { storage } from '../storage';

interface TelegramUser {
  id: number;
  first_name: string;
  last_name?: string;
  username?: string;
}

interface TelegramMessage {
  message_id: number;
  from: TelegramUser;
  chat: {
    id: number;
    type: string;
  };
  text?: string;
  date: number;
}

interface TelegramUpdate {
  update_id: number;
  message?: TelegramMessage;
  callback_query?: any;
}

class TelegramService {
  private botToken: string;
  private webhookPath: string;

  constructor() {
    this.botToken = process.env.TELEGRAM_BOT_TOKEN || '';
    this.webhookPath = process.env.TELEGRAM_WEBHOOK_SECRET_PATH || '/webhook/telegram/secret';

    if (!this.botToken) {
      throw new Error('TELEGRAM_BOT_TOKEN is required');
    }
  }

  async sendMessage(chatId: number, text: string, options: any = {}) {
    const url = `https://api.telegram.org/bot${this.botToken}/sendMessage`;

    try {
      const response = await fetch(url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          chat_id: chatId,
          text,
          ...options,
        }),
      });

      if (!response.ok) {
        console.error('Failed to send Telegram message:', await response.text());
      }

      return await response.json();
    } catch (error) {
      console.error('Error sending Telegram message:', error);
      throw error;
    }
  }

  async sendPhoto(chatId: number, photo: string, caption?: string, options: any = {}) {
    const url = `https://api.telegram.org/bot${this.botToken}/sendPhoto`;

    try {
      const response = await fetch(url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          chat_id: chatId,
          photo,
          caption,
          ...options,
        }),
      });

      return await response.json();
    } catch (error) {
      console.error('Error sending Telegram photo:', error);
      throw error;
    }
  }

  async handleWebhook(req: Request, res: Response) {
    try {
      const update: TelegramUpdate = req.body;

      if (update.message) {
        await this.handleMessage(update.message);
      } else if (update.callback_query) {
        await this.handleCallbackQuery(update.callback_query);
      }

      res.sendStatus(200);
    } catch (error) {
      console.error('Error handling Telegram webhook:', error);
      res.sendStatus(500);
    }
  }

  private async handleMessage(message: TelegramMessage) {
    const chatId = message.chat.id;
    const text = message.text || '';
    const user = message.from;

    // Register or update user
    let dbUser = await storage.getUserByTelegramId(user.id.toString());
    if (!dbUser) {
      dbUser = await storage.createUser({
        telegramId: user.id.toString(),
        username: user.username,
        firstName: user.first_name,
        lastName: user.last_name,
      });
    }

    if (text.startsWith('/start')) {
      await this.handleStartCommand(chatId, dbUser);
    } else if (text.startsWith('/menu')) {
      await this.showMainMenu(chatId);
    } else if (text.startsWith('/browse')) {
      await this.showCategories(chatId);
    } else if (text.startsWith('/cart')) {
      await this.showCart(chatId, dbUser.id);
    } else if (text.startsWith('/orders')) {
      await this.showOrders(chatId, dbUser.id);
    } else if (text.startsWith('/support')) {
      await this.showSupport(chatId);
    } else {
      await this.handleTextMessage(chatId, text, dbUser);
    }
  }

  private async handleCallbackQuery(callbackQuery: any) {
    const chatId = callbackQuery.message.chat.id;
    const data = callbackQuery.data;
    const messageId = callbackQuery.message.message_id;

    // Answer callback query to remove loading state
    await fetch(`https://api.telegram.org/bot${this.botToken}/answerCallbackQuery`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ callback_query_id: callbackQuery.id }),
    });

    if (data.startsWith('category_')) {
      const categoryId = data.replace('category_', '');
      await this.showProductsInCategory(chatId, categoryId);
    } else if (data.startsWith('product_')) {
      const productId = data.replace('product_', '');
      await this.showProductDetails(chatId, productId);
    } else if (data.startsWith('add_to_cart_')) {
      const productId = data.replace('add_to_cart_', '');
      await this.addToCart(chatId, productId);
    } else if (data.startsWith('checkout')) {
      await this.startCheckout(chatId);
    }
  }

  private async handleStartCommand(chatId: number, user: any) {
    const welcomeMessage = `
ðŸ›ï¸ **Welcome to EcomBot!**

Hi ${user.firstName || 'there'}! I'm your personal shopping assistant. 

Here's what you can do:
â€¢ ðŸ“± Browse products by category
â€¢ ðŸ” Search for specific items  
â€¢ ðŸ›’ Add items to your cart
â€¢ ðŸ’³ Pay securely with MTN Mobile Money
â€¢ ðŸ“¦ Track your orders

Let's get started! Use the menu below or type /menu anytime.
    `;

    await this.sendMessage(chatId, welcomeMessage, {
      parse_mode: 'Markdown',
      reply_markup: {
        keyboard: [
          [{ text: 'ðŸ“± Browse Products' }, { text: 'ðŸ›’ My Cart' }],
          [{ text: 'ðŸ“¦ My Orders' }, { text: 'ðŸ” Search' }],
          [{ text: 'ðŸ’¬ Support' }, { text: 'ðŸ‘¤ Profile' }]
        ],
        resize_keyboard: true,
        one_time_keyboard: false
      }
    });
  }

  private async showMainMenu(chatId: number) {
    const menuMessage = `
ðŸ  **Main Menu**

Choose an option below:
    `;

    await this.sendMessage(chatId, menuMessage, {
      parse_mode: 'Markdown',
      reply_markup: {
        inline_keyboard: [
          [
            { text: 'ðŸ“± Browse Categories', callback_data: 'browse_categories' },
            { text: 'ðŸ” Search Products', callback_data: 'search_products' }
          ],
          [
            { text: 'ðŸ›’ View Cart', callback_data: 'view_cart' },
            { text: 'ðŸ“¦ My Orders', callback_data: 'my_orders' }
          ],
          [
            { text: 'ðŸ’¬ Contact Support', callback_data: 'support' },
            { text: 'ðŸ‘¤ My Profile', callback_data: 'profile' }
          ]
        ]
      }
    });
  }

  private async showCategories(chatId: number) {
    const categories = await storage.getAllCategories();

    if (categories.length === 0) {
      await this.sendMessage(chatId, 'ðŸ“± No categories available at the moment. Please check back later!');
      return;
    }

    const inlineKeyboard = categories.map(category => ([
      { text: category.name, callback_data: `category_${category.id}` }
    ]));

    await this.sendMessage(chatId, 'ðŸ“± **Product Categories**\n\nChoose a category to browse:', {
      parse_mode: 'Markdown',
      reply_markup: { inline_keyboard: inlineKeyboard }
    });
  }

  private async showProductsInCategory(chatId: number, categoryId: string) {
    const products = await storage.getProductsByCategoryId(categoryId);

    if (products.length === 0) {
      await this.sendMessage(chatId, 'ðŸ“± No products available in this category.');
      return;
    }

    for (const product of products.slice(0, 10)) { // Show first 10 products
      const message = `
ðŸ›ï¸ **${product.title}**

ðŸ’° Price: â‚µ${product.priceGhs}
ðŸ“¦ Stock: ${product.stock} available
ðŸ“‹ SKU: ${product.sku}

${product.description || 'No description available'}
      `;

      const keyboard = [
        [{ text: 'ðŸ›’ Add to Cart', callback_data: `add_to_cart_${product.id}` }],
        [{ text: 'ðŸ“± View Details', callback_data: `product_${product.id}` }]
      ];

      if (product.images && product.images.length > 0) {
        await this.sendPhoto(chatId, product.images[0], message, {
          parse_mode: 'Markdown',
          reply_markup: { inline_keyboard: keyboard }
        });
      } else {
        await this.sendMessage(chatId, message, {
          parse_mode: 'Markdown',
          reply_markup: { inline_keyboard: keyboard }
        });
      }
    }
  }

  private async showProductDetails(chatId: number, productId: string) {
    const product = await storage.getProduct(productId);

    if (!product) {
      await this.sendMessage(chatId, 'âŒ Product not found.');
      return;
    }

    const message = `
ðŸ›ï¸ **${product.title}**

ðŸ’° **Price:** â‚µ${product.priceGhs}
ðŸ“¦ **Stock:** ${product.stock} available
ðŸ“‹ **SKU:** ${product.sku}

ðŸ“ **Description:**
${product.description || 'No description available'}

${product.tags && product.tags.length > 0 ? `ðŸ·ï¸ **Tags:** ${product.tags.join(', ')}` : ''}
    `;

    const keyboard = [
      [{ text: 'ðŸ›’ Add to Cart', callback_data: `add_to_cart_${product.id}` }],
      [{ text: 'ðŸ”™ Back to Categories', callback_data: 'browse_categories' }]
    ];

    if (product.images && product.images.length > 0) {
      await this.sendPhoto(chatId, product.images[0], message, {
        parse_mode: 'Markdown',
        reply_markup: { inline_keyboard: keyboard }
      });
    } else {
      await this.sendMessage(chatId, message, {
        parse_mode: 'Markdown',
        reply_markup: { inline_keyboard: keyboard }
      });
    }
  }

  private async addToCart(chatId: number, productId: string) {
    // In a real implementation, you'd store cart items in a session or database
    // For now, we'll just show a success message
    const product = await storage.getProduct(productId);

    if (!product || product.stock <= 0) {
      await this.sendMessage(chatId, 'âŒ Sorry, this product is not available.');
      return;
    }

    await this.sendMessage(chatId, `âœ… **${product.title}** added to your cart!\n\nðŸ’° Price: â‚µ${product.priceGhs}`, {
      parse_mode: 'Markdown',
      reply_markup: {
        inline_keyboard: [
          [
            { text: 'ðŸ›’ View Cart', callback_data: 'view_cart' },
            { text: 'ðŸ’³ Checkout', callback_data: 'checkout' }
          ],
          [{ text: 'ðŸ”™ Continue Shopping', callback_data: 'browse_categories' }]
        ]
      }
    });
  }

  private async showCart(chatId: number, userId: string) {
    // Placeholder for cart functionality
    await this.sendMessage(chatId, 'ðŸ›’ **Your Cart**\n\nYour cart is currently empty.\nUse /browse to start shopping!', {
      parse_mode: 'Markdown',
      reply_markup: {
