  }

  private async showOrders(chatId: number, userId: string) {
    const orders = await storage.getOrdersByUserId(userId);

    if (orders.length === 0) {
      await this.sendMessage(chatId, 'ðŸ“¦ **Your Orders**\n\nYou haven\'t placed any orders yet.\nStart shopping to see your orders here!', {
        parse_mode: 'Markdown'
      });
