    }

    let ordersText = 'ðŸ“¦ **Your Recent Orders**\n\n';

    for (const order of orders.slice(0, 5)) {
      ordersText += `ðŸ†” Order: ${order.orderNumber}\n`;
      ordersText += `ðŸ’° Total: â‚µ${order.totalGhs}\n`;
      ordersText += `ðŸ“Š Status: ${order.status}\n`;
      ordersText += `ðŸ“… Date: ${order.createdAt.toLocaleDateString()}\n\n`;
